	if err = randomize.Struct(seed, &c, episodeDBTypes, false, episodeColumnsWithDefault...); err != nil {
		t.Fatal(err)
	}

	b.PodcastID = a.ID
	c.PodcastID = a.ID

	if err = b.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = c.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}

	check, err := a.Episodes().All(ctx, tx)
	if err != nil {
		t.Fatal(err)
	}

	bFound, cFound := false, false
	for _, v := range check {
		if v.PodcastID == b.PodcastID {
			bFound = true
		}
		if v.PodcastID == c.PodcastID {
			cFound = true
		}
	}

	if !bFound {
		t.Error("expected to find b")
	}
	if !cFound {
		t.Error("expected to find c")
	}

	slice := PodcastSlice{&a}
	if err = a.L.LoadEpisodes(ctx, tx, false, (*[]*Podcast)(&slice), nil); err != nil {
		t.Fatal(err)
	}
	if got := len(a.R.Episodes); got != 2 {
		t.Error("number of eager loaded records wrong, got:", got)
	}

	a.R.Episodes = nil
	if err = a.L.LoadEpisodes(ctx, tx, true, &a, nil); err != nil {
		t.Fatal(err)
	}
	if got := len(a.R.Episodes); got != 2 {
		t.Error("number of eager loaded records wrong, got:", got)
	}

	if t.Failed() {
		t.Logf("%#v", check)
	}
