func testPodcastToManyAddOpEpisodes(t *testing.T) {
	var err error

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()

	var a Podcast
	var b, c, d, e Episode

	seed := randomize.NewSeed()
	if err = randomize.Struct(seed, &a, podcastDBTypes, false, strmangle.SetComplement(podcastPrimaryKeyColumns, podcastColumnsWithoutDefault)...); err != nil {
		t.Fatal(err)
	}
	foreigners := []*Episode{&b, &c, &d, &e}
	for _, x := range foreigners {
		if err = randomize.Struct(seed, x, episodeDBTypes, false, strmangle.SetComplement(episodePrimaryKeyColumns, episodeColumnsWithoutDefault)...); err != nil {
			t.Fatal(err)
		}
	}

	if err := a.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = b.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = c.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}

	foreignersSplitByInsertion := [][]*Episode{
		{&b, &c},
		{&d, &e},
	}

	for i, x := range foreignersSplitByInsertion {
		err = a.AddEpisodes(ctx, tx, i != 0, x...)
		if err != nil {
			t.Fatal(err)
		}

		first := x[0]
		second := x[1]

		if a.ID != first.PodcastID {
			t.Error("foreign key was wrong value", a.ID, first.PodcastID)
		}
		if a.ID != second.PodcastID {
			t.Error("foreign key was wrong value", a.ID, second.PodcastID)
		}

		if first.R.Podcast != &a {
			t.Error("relationship was not added properly to the foreign slice")
		}
		if second.R.Podcast != &a {
			t.Error("relationship was not added properly to the foreign slice")
		}

		if a.R.Episodes[i*2] != first {
			t.Error("relationship struct slice not set to correct value")
		}
		if a.R.Episodes[i*2+1] != second {
			t.Error("relationship struct slice not set to correct value")
		}

		count, err := a.Episodes().Count(ctx, tx)
		if err != nil {
			t.Fatal(err)
		}
		if want := int64((i + 1) * 2); count != want {
			t.Error("want", want, "got", count)
		}
	}
}

